package _2;

import java.util.Arrays;

/**
 * Created by kinder112 on 03.12.2016.
 */
public class _2 {

    private static final String input = "\n" +
            "\n" +
            "\n" +
            "\n" +
            "";
    private static KeyPad currentKey = KeyPad._5;
    private static KeyPad2 currentKey2 = KeyPad2._5;

    public static void main(String[] args) {
        final String[] lines = input.split("\n");
        Arrays.stream(lines).forEach(line -> parseLine(line));
    }

    private static void parseLine(String line) {
        Arrays.stream(line.split("")).forEach(c -> parseStep(c));
        System.out.println("*" + currentKey);
        System.out.println("**********" + currentKey2);
    }

    private static void parseStep(String move) {
        switch (move) {
            case "U":
                currentKey = currentKey.up();
                currentKey2 = currentKey2.up();
                break;
            case "D":
                currentKey = currentKey.down();
                currentKey2 = currentKey2.down();
                break;
            case "L":
                currentKey = currentKey.left();
                currentKey2 = currentKey2.left();
                break;
            case "R":
                currentKey = currentKey.right();
                currentKey2 = currentKey2.right();
                break;
        }
    }

}
